<style lang="less" scoped>
.message-section {
    padding: 36px 0 16px 0;
    background-color: #deefee;
    border: 8px solid #ffdcdc;
  
    .title {
        font-family: 'SVN-Saint Monica', serif;
        font-size: 36px;
        letter-spacing: 0.05em;
        text-transform: uppercase;
        color: @dark-gray;
        position: relative;
        text-align: center;
        z-index: 2;

        &::before, &::after{
            content: '';
            width: 16px;
            height: 100%;
            background-color: white;
            position: absolute;
            top: 0;
            z-index: 1;
        }

        &::before {
            left: 0;
        }

        &::after {
            right: 0;
        }
    }

    .images-card {
        display: flex;
        justify-content: center;
        margin-top: 16px;
    }

    .message-content {
        margin-top: 16px;
        padding: 16px 32px;
        display: flex;
        flex-direction: column;
        align-items: end;

        .content {
            font-family: "Inter", sans-serif;
            font-weight: 400;
            font-size: 16px;
            line-height: 170%;
            text-align: justify;
            color: @darker-gray;
        }

        .signature {
            margin-top: 16px;
            width: fit-content;
            display: flex;
            flex-direction: column;
            align-items: center;
            
            .subtitle {
                font-family: "Inter", sans-serif;
                font-weight: 600;
                font-size: 16px;
                text-align: center;
                color: @darker-gray;

                span {
                    position: relative;
                    top: 5px;
                    margin-left: 4px;
                }
            }
        }
    }
}
